day6_input = ""


# Part 1
# How many characters need to be processed before the first start-of-packet marker is detected?
def find_packet(m_len):
    for i in range(m_len,len(day6_input)):
        slice = day6_input[i-m_len:i]
        if len(slice) == len(set(slice)):
            break
    return (slice, i)
    # print("DAY 6 (part 1) chars to be processed [%s]: %d " % (slice, i))


if __name__ == "__main__":
    sol = find_packet(4)
    print("DAY 6 (part 1) chars to be processed [%s]: %d " % sol)
    sol = find_packet(14)
    print("DAY 6 (part 2) chars to be processed [%s]: %d " % sol)